svg.script:
   /*
   NOTE:
   Mozilla 0.96 with Adobe SVG plugin does not support:
   ->  Exporting the svgdoc handle to the outer HTML
   ->  Accessing the embeded SVG from out HTML with
       document.embeds["fritz"].getSVGDocument()
   ->  Reported to bugzilla # 115528
   */
	function on_load(evt) {
		svgdoc = evt.getCurrentNode().getOwnerDocument();
      alert("svg: svgdoc=" + svgdoc);
      var owner = svgdoc.ownerDocument;
      alert("svg: ownerDocument=" + owner);
   }

   width = 20;
   function change_attribut(evt) {
      svgdoc=evt.getTarget().getOwnerDocument();
      objet=svgdoc.getElementById('rectangle');
      objet.setAttribute('width',width);
      width += 5;
   }
