requirement.example:

// Construct a message and send it to 'Gesa', 'Tim' and 'Ben'
String xmlKey = "<key oid='' contentMime='text/plain'>\n" +
                "</key>";

String qos = "<qos>" +
             "   <destination queryType='EXACT'>" +
             "      Gesa" +
             "   </destination>" +
             "   <destination queryType='EXACT'>" +
             "      Tim" +
             "   </destination>" +
             "   <destination queryType='EXACT'>" +
             "      Ben" +
             "   </destination>" +
             "</qos>";

content = "Dinner is ready";
MessageUnit msgUnit = new MessageUnit(xmlKey, content.getBytes());
try {
   xmlBlaster.publish(msgUnit, qos);
} catch(XmlBlasterException e) {
   System.err.println("Failed to send message");
}
