requirement.example:

// Construct a message and send it to 'Michelle', she is currently not logged in
String xmlKey = "<key oid='' contentMime='text/plain'>\n" +
                "</key>";

String qos = "<qos>" +
             "   <destination queryType='EXACT'>" +
             "      Michelle" +
             "   </destination>" +
             "</qos>";

content = "Hi Michelle";
MessageUnit msgUnit = new MessageUnit(xmlKey, content.getBytes());
try {
   xmlBlaster.publish(msgUnit, qos);
} catch(XmlBlasterException e) {
   System.out.println("Exception is correct, Michelle is not logged in");
}
