requirement.example:

 -----------------------------------------------
|The settings in the xmlBlaster.properties-file |
 -----------------------------------------------
PDOM.Cachesize=3000000
PDOM.Dbpath=${user.home}/tmp
PDOM.Messagesize=5000

// QoS with durable-flag (persistent-flag).
<qos>
   <isDurable />
</qos> 

// Construct a MessageUnit which is persistent.
String xmlKey = "<key oid='sender'>\n" +
                   "<person pid='10' gid='200'>" +
                      "<name age='31' sex='f'>Lisa</name>"+
                   "</person>" +
                "</key>"; 

String qos = "<qos> \n" +
                 "<isDurable />" +
             "</qos>";

String content = "Data for personKey and some other data";

MessageUnit mu = new MessageUnit(xmlKey, content.getBytes());

// Get the xmldb (PDOM) and insert the MessageUnit.

PDOM pdom = PDOM.getInstance();
pdom.insert(mu);
