description.pre:
typedef string XmlType;
typedef sequence<string> StringArr;

XmlType subscribe(in XmlType xmlKey, in XmlType qos) raises(XmlBlasterException);

example.pre:
import org.xmlBlaster.client.*;
...

SubscribeKeyWrapper sk = new SubscribeKeyWrapper("MyOid");

SubscribeQosWrapper sq = new SubscribeQosWrapper();

SubscribeRetQos sr = con.subscribe(sk.toXml(), sq.toXml());

System.out.println("Success, subscriptionId=" + sr.getSubscriptionId());

import org.xmlBlaster.client.*;
...

SubscribeKeyWrapper sk = new SubscribeKeyWrapper("/xmlBlaster/key[@oid='radar.track']",
                         Constants.XPATH);

SubscribeQosWrapper sq = new SubscribeQosWrapper();

SubscribeRetQos sr = con.subscribe(sk.toXml(), sq.toXml());

System.out.println("Success, subscriptionId=" + sr.getSubscriptionId());

import org.xmlBlaster.client.*;
...

SubscribeKeyWrapper sk = new SubscribeKeyWrapper("MyMessageOid");

sk.setDomain("RUGBY_NEWS"); // Subscription is forwarded from slaves to master
// without setting the domain the subscribe would just be handled by the slave connected to

SubscribeRetQos sr = con.subscribe(sk.toXml(), null);

System.out.println("Success, subscriptionId=" + sr.getSubscriptionId());
